import { takeLatest, call, put, all } from 'redux-saga/effects';

import { auth, googleProvider } from '../../../firebase/firebase.util';
import { SAGA_EMAIL_USER_START, SAGA_GOOGLE_USER_START } from './type';
import { sagaEmailUserSuccess, sagaEmailUserFailed, sagaGoogleUserFailed, sagaGoogleUserSUCCESS } from './user-action';



function* startGoogle() {
    try {
        const data = yield auth.signInWithPopup(googleProvider);

        console.log(data)
    } catch (e) {
        yield put(sagaGoogleUserFailed(e.message));
    }
}

export function* sagaGoogleUserStart() {
    yield takeLatest(SAGA_GOOGLE_USER_START, startGoogle);
}

export function* userSagas() {
    yield all([call(sagaGoogleUserStart)]);
}
