import { TOGGLE_CART, ADD_ITEM } from './type';

const defaultState = {
    hidden: true,
    items: []
};

export default (state = defaultState, action) => {
    switch(action.type) {
        case TOGGLE_CART:
            return {
                ...state,
                hidden: !state.hidden
            };

        case ADD_ITEM:
            return [...state.items, action.payload];

        default:
            return state;
    }
};
