import { TOGGLE_CART } from './type';

const defaultState = {
    hidden: true
};

export default (state = defaultState, action) => {
    switch(action.type) {
        case TOGGLE_CART:
            return {
                ...state,
                hidden: !state.hidden
            };

        default:
            return state;
    }
};
