import { takeLatest, call, put, all } from 'redux-saga/effects';

import { auth, googleProvider, convertCollectionsSnapshotToMap, createUser } from '../../../firebase/firebase.util';
import { SAGA_EMAIL_USER_START, SAGA_GOOGLE_USER_START } from './type';
import { sagaUserSUCCESS, sagaUserFailed } from './user-action';



function* startGoogle() {
    try {

        const { user } = yield auth.signInWithPopup(googleProvider);

        const data = yield call(convertCollectionsSnapshotToMap, user);
        const userSnapshot = yield data.get();

        yield put(sagaUserSUCCESS({id: userSnapshot.id, ...userSnapshot.data()}))
    } catch (e) {
        yield put(sagaUserFailed(e.message));
    }
}

export function* sagaGoogleUserStart() {

    yield takeLatest(SAGA_GOOGLE_USER_START, startGoogle);
}

export function* getSnapshotFromUserAuth(userAuth, additionalData) {
    try {
        const userRef = yield call(createUser, userAuth, additionalData );
        const userSnapshop = yield userRef.get();
        yield put(sagaUserSUCCESS({ id: userSnapshop.id, ...userSnapshop.data()}))
    }catch(e) {
        put(sagaUserFailed(e.message));
    }
}


function* startEmail({ payload: { email, password }}) {
    try {
        const { user } = yield auth.signInWithEmailAndPassword(email, password);
        yield getSnapshotFromUserAuth(user);
    }catch(e) {
        yield put(sagaUserFailed(e.message));
    }
}

export function* sagaEmailUserStart() {
    yield takeLatest(SAGA_EMAIL_USER_START, startEmail);
}

export function* userSagas() {
    yield all([call(sagaGoogleUserStart), call(sagaEmailUserStart)]);
}
