import { CREATE_CURRENT_USER,
         SAGA_USER_SUCCESS,
         SAGA_USER_FAILED } from './type';

const defaultState = {
    currentUser: null,
    error: null
};

export default (state = defaultState, action) => {
    switch(action.type) {
        case CREATE_CURRENT_USER:
            return {
                ...state,
                currentUser: action.payload
            };

        case SAGA_USER_SUCCESS:
            return {
                ...state,
                currentUser: action.payload,
                error: null
            }

        case SAGA_USER_FAILED:
            return {
                ...state,
                currentUser: null,
                error: action.payload
            }

        default:
            return state;
    }
};
